var DoH = Enumerable.Range(8, 7);
var gearSets = GearsetManager.GearSets.Where(i => i.InUse && DoH.Contains((int)i.Class) && Core.Player.Levels[i.Class] > 69).ToList().OrderBy(y => Core.Player.Levels[y.Class]);
if (gearSets.Any())
{
  gearSets.First().Activate();
  await Coroutine.Sleep(2000);
}


var Combat = Enumerable.Range(8, 11).Append(0).Append(36);
var gearSets = GearsetManager.GearSets.Where(i => i.InUse && !Combat.Contains((int)i.Class)).ToList().OrderByDescending(i=> i.Gear.First().Item.ItemLevel);
if (gearSets.Any())
{
  gearSets.First().Activate();
  await Coroutine.Sleep(2000);
}


var npcId = GameObjectManager.GetObjectByNPCId(1009221);

	if (!npcId.IsWithinInteractRange)

		{
			var _target = npcId.Location;
			Navigator.PlayerMover.MoveTowards(_target);
			while (_target.Distance2D(Core.Me.Location) >= 4)
				{
					Navigator.PlayerMover.MoveTowards(_target);
					await Coroutine.Sleep(100);
				}
					Navigator.PlayerMover.MoveStop();
				}

	npcId.Interact();

	await Coroutine.Wait(10000, () => SelectIconString.IsOpen);

	if (!SelectIconString.IsOpen)
	{
		npcId.Interact();

		await Coroutine.Wait(10000, () => SelectIconString.IsOpen);
	}

	await Buddy.Coroutines.Coroutine.Sleep(500);
	{
		Logging.WriteDiagnostic("Choosing current quest.");
		if (ConditionParser.HasQuest(67059))
			SelectIconString.ClickLineContains(("Ishgard"));
		else if (ConditionParser.HasQuest(67102))
			SelectIconString.ClickLineContains(("Ectoplasm"));
	}					

	await Coroutine.Wait(5000, () => Talk.DialogOpen);
	await Buddy.Coroutines.Coroutine.Sleep(500);
	while (Talk.DialogOpen)
	{
		Talk.Next();
		await Coroutine.Wait(500, () => !Talk.DialogOpen);
		await Coroutine.Wait(500, () => Talk.DialogOpen);
		await Coroutine.Yield();
	}


