foreach (var plugin in PluginManager.Plugins.Where(p => p.Plugin.Name == "Enable Flight" || p.Plugin.Name == "启用飞行"))
{
  try
  {
    if (!plugin.Enabled)  {  break;  }  else  {  plugin.Enabled = false;  }
  }
  catch (Exception ex) {  }
}


var currentStep = ConditionParser.GetQuestById(67813).GetTodoArgs(1, 0).Item1;
var ids = new HashSet<int>() { 1016871, 1016872, 1016873, 1016874 };

if (Core.Me.IsMounted)  {  await CommonTasks.StopAndDismount();  }

await Coroutine.Sleep(2000);

var obj = GameObjectManager.GetObjectsOfType<GameObject>(true, false).Where(r => r.IsTargetable && ids.Contains((int)r.NpcId)).FirstOrDefault();

obj.Face();
obj.Target();

ChatManager.SendChat("/s Uhm Ala");

while (ConditionParser.GetQuestStep(67813) == 2)
{
  if (Talk.DialogOpen)  {  Talk.Next();  }
  await Coroutine.Yield();
}

while (ConditionParser.GetQuestStep(67813) == 1 && ConditionParser.GetQuestById(67813).GetTodoArgs(1, 0).Item1 == currentStep)
{
  if (Talk.DialogOpen)  {  Talk.Next();  }
  await Coroutine.Yield();
