var v1 = new Vector3(220.534f, 55.52545f, -797.7146f);
var v2 = new Vector3(211.495f, 60.53117f, -911.8719f);
var p1 = new ff14bot.Pathing.FlyToParameters(v1) { CheckIndoors = false };
var p2 = new ff14bot.Pathing.FlyToParameters(v2) { CheckIndoors = false };

if (!Core.Player.IsMounted)  {  await CommonTasks.SummonFlyingMount();  }

await CommonTasks.TakeOff();
await CommonTasks.AscendTo(55f);		
MovementManager.MoveStop();		

while (Core.Player.Location.Distance(v1) > 3f)
{
   ff14bot.Pathing.Flightor.MoveTo(p1);
   await Coroutine.Yield();
}

while (Core.Player.Location.Distance(v2) > 2f)
{
   ff14bot.Pathing.Flightor.MoveTo(p2);
   await Coroutine.Yield();
}

MovementManager.MoveStop();
await CommonTasks.Land();


